package com.weslleycabral.workshopmongodb.resources;import com.weslleycabral.workshopmongodb.dto.UserDTO;import com.weslleycabral.workshopmongodb.entities.User;import com.weslleycabral.workshopmongodb.services.UserService;import jakarta.websocket.server.PathParam;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping(value = "/users")public class UserResource {    @Autowired    private UserService service;    @GetMapping    public ResponseEntity<List<UserDTO>> findAll() {        return ResponseEntity.ok().body(service.findAll());    }    @GetMapping(value = "/{id}")    public ResponseEntity<UserDTO> findById(@PathVariable String id) {        return ResponseEntity.ok().body(service.findById(id));    }}