package com.weslleycabral.workshopmongodb.services;import com.weslleycabral.workshopmongodb.entities.User;import com.weslleycabral.workshopmongodb.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;import java.util.Optional;@Servicepublic class UserService {    @Autowired    private UserRepository repository;    public List<User> findAll() {        List<User> list = new ArrayList<>();        list = repository.findAll();        return list;    }    public User findById(String id) {       Optional<User> user = repository.findById(id);        return user.orElseThrow();    }}